create procedure col_inconsitencia_pagos
	-- =============================================
	-- Author:      <Fabio>
	-- Create date: <2019-12-02 10:04:00.317>
	-- Description: <Realiza la insercion(opcion 1) a la tabla "col_cpbt_carga_pagos_bancos_temporal" y devuelve (opcion 2) los pagos inconsitentes>
	-- =============================================
	-- col_inconsitencia_pagos 1, 6121778, 'Pago en Linea PUNTOXPRESS (NPE)', '15/11/2019' -- NO FALTA EN EXCEL
	-- col_inconsitencia_pagos 1, 6122384, 'Pago en Linea PUNTOXPRESS (NPE)', '15/11/2019' -- FALTA EN EXCEL
	-- col_inconsitencia_pagos 1, 6126225, 'Pago en Linea PUNTOXPRESS (NPE)', '15/11/2019' -- FALTA EN EXCEL

	-- col_inconsitencia_pagos 2, 0, 'Pago en Linea PUNTOXPRESS (NPE)', '15/11/2019' -- VERIFICA LA INCONSITENCIA DE PAGOS

	-- col_inconsitencia_pagos 3, 0, '', '' --BORRA LA DATA DE LA TABLA TEMPORAL
	@opcion int = 0,
	@codmov int = 0,
	@mov_descripcion varchar(125) = '',
	@mov_fecha_registro nvarchar(12) = ''
as
begin
	set dateformat dmy
	if @opcion = 1
	begin
		insert into col_cpbt_carga_pagos_bancos_temporal (cpbt_codmov, cpbt_mov_descripcion, cpbt_mov_fecha_registro)
		values (@codmov, @mov_descripcion, @mov_fecha_registro)
	end

	if @opcion = 2
	begin
		if (select count(1) from col_cpbt_carga_pagos_bancos_temporal  where cpbt_mov_descripcion = @mov_descripcion and cpbt_mov_fecha_registro = @mov_fecha_registro) > 0
		begin
			declare @tbl_alumnos_faltantes as table (per_codigo int, per_carnet varchar(12), mov_codigo int, tmo_arancel varchar(25), mov_descripcion varchar(50), mov_estado varchar(10), per_apellidos_nombres varchar(125), mov_fecha_real_pago datetime)
			--select cpbt_codmov from col_cpbt_carga_pagos_bancos_temporal where cpbt_mov_descripcion = @mov_descripcion and cpbt_mov_fecha_registro = @mov_fecha_registro
			insert into @tbl_alumnos_faltantes (per_codigo, per_carnet, mov_codigo, tmo_arancel ,mov_descripcion, mov_estado, per_apellidos_nombres, mov_fecha_real_pago )
			select per_codigo, per_carnet, mov_codigo, tmo_arancel ,mov_descripcion, mov_estado, per_apellidos_nombres, mov_fecha_real_pago 
			from col_mov_movimientos 
			inner join ra_per_personas on per_codigo = mov_codper
			inner join col_dmo_det_mov on dmo_codmov = mov_codigo
			inner join col_tmo_tipo_movimiento on tmo_codigo = dmo_codtmo
			where mov_descripcion = @mov_descripcion--'Pago en Linea PUNTOXPRESS (NPE)'
			and mov_codigo not in (select cpbt_codmov from col_cpbt_carga_pagos_bancos_temporal where cpbt_mov_descripcion = @mov_descripcion and cpbt_mov_fecha_registro = @mov_fecha_registro)
			and convert(nvarchar(12), mov_fecha_registro,103) = @mov_fecha_registro--'15/11/2019' 

			if (select count(1) from @tbl_alumnos_faltantes )= 0
			begin
				select 'Sin alumnos faltantes' 'Resultado'
			end
			else
			begin
				select  row_number() over(order by per_codigo) 'Nª', * from @tbl_alumnos_faltantes
			end
		end
		else
		begin
			print 'No hay datos subidos para validar'
		end
	end

	if @opcion = 3 -- borra la tabla "col_cpbt_carga_pagos_bancos_temporal"
	begin
		delete from col_cpbt_carga_pagos_bancos_temporal
		dbcc checkident (col_cpbt_carga_pagos_bancos_temporal,reseed,0)
	end
end